import React, { Component } from "react";
import { render } from "react-dom";

import Select from "react-select";

import "react-select/dist/react-select.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: "React",
      selectedPark: {},
      selectedResturant: {}
    };
  }

  handleChange1 = selectedPark => {
    this.setState({ selectedPark });
  };

  handleChange2 = selectedPark => {
    this.setState({ selectedResturant: selectedPark });
  };

  render() {
    const options1 = [
      { value: "one", label: "One" },
      { value: "two", label: "Two" }
    ];

    const options2 = [
      { value: "one-a", label: "One A", link: "one" },
      { value: "one-b", label: "One B", link: "one" },
      { value: "two-a", label: "Two A", link: "two" },
      { value: "two-b", label: "Two B", link: "two" }
    ];

    const filteredOptions = options2.filter(
      o => o.link === this.state.selectedPark.value
    );

    return (
      <div>
        <p>Select one first</p>
        <Select
          name="form-field-name"
          value={this.state.selectedPark.value}
          onChange={this.handleChange1}
          options={options1}
        />
        <p>Then the other</p>
        <Select
          name="form-field-name"
          value={this.state.selectedResturant.value}
          onChange={this.handleChange2}
          options={filteredOptions}
        />
      </div>
    );
  }
}

render(<App />, document.getElementById("root"));
